 query acountQuery($accountId: Int!) {
   account(accountId: $accountId) {
     ...accountFragment
   }
 }

 fragment accountFragment on Account {

id 
firstName 
lastName 
email 
phoneNumber 
title 
certifications 
hoursOfOperation
 }

